import SearchBar from './SearchBar';

class App extends React.Component {
    onTermSubmit = (term) =>{
        console.log(this.state.term);

    };
    render() {
        return (
        <div><SearchBar onFormSubmit={this.onTermSubmit} /></div>
        );
    }
}
 export default App;
